import React from 'react';
import 'react-vis/dist/style.css';
import { SingletonSocket } from '../SingletonSocket';
import {RadarChart, CircularGridLines } from 'react-vis';
import * as d3 from 'd3';
import { Box, Flex, Grid, GridItem, Skeleton } from '@chakra-ui/react';

// This radial chart is specifically only for showing scores out of 5.
interface ResponseStruct {
	angle: number;
	radius: number;
	label: string;
	subLabel?: string;
}

interface DataProps {
	dataStorage: ResponseStruct[];
	dataPipe: string[];
	socketConnection: any;
	value?: any;
	items?: any;
}

class RatingChart extends React.Component<
	{ dataPipe: string[]; height: number; width: number },
	DataProps
> {
	constructor(props) {
		super(props);
		var itemArray = [];
		for (let i = 0; i < this.props.dataPipe.length; i++) {
			itemArray.push({ title: this.props.dataPipe[i] });
		}
		this.state = {
			dataStorage: [],
			dataPipe: this.props.dataPipe,
			socketConnection: SingletonSocket.getInstance().getSocket(),
			items: itemArray,
			value: null,
		};
	}

	componentDidMount() {
		const { dataPipe, socketConnection, dataStorage } = this.state;
		for (let i = 0; i < dataPipe.length; i++) {
			socketConnection.on(dataPipe[i], (data) => {
				dataStorage.splice(i, 1, data);
				this.setState({ dataStorage: dataStorage });
			});
		}
	}
	componentWillUnmount() {
		const { dataPipe, socketConnection } = this.state;
		for (let i = 0; i < dataPipe.length; i++) {
			socketConnection.off(dataPipe[i], (data) => {});
		}
	}

	render() {
		const { dataStorage, value, items } = this.state;
		const data = {
			data: [
				{
					explosions: 3.2,
					wow: 4.7,
					dog: 4.8,
					sickMoves: 2,
					nice: 1.9,
				},
			],
			domains: [
				{ name: 'nice', domain:[0,5],},
				{ name: 'explosions', domain: [0, 5] },
				{ name: 'wow', domain: [0, 5] },
				{ name: 'dog', domain: [0, 5] },
				{ name: 'sickMoves', domain: [0, 5] },
			],
		};
		console.log([...new Array(6)].map((v, i) => i / 6 - 1));
		return (
			<Flex p="1">
				{dataStorage.length == 0 ? (
					<Skeleton
						height={this.props.height.toString() + 'px'}
						width={this.props.width.toString() + 'px'}
					></Skeleton>
				) : (
					<>
						<RadarChart
							width={this.props.width}
							height={this.props.height}
							data={data.data}
							domains={data.domains}
							style={{
								polygons: {
									fillOpacity: 0,
									strokeWidth: 3,
								},
								axes: {
									text: {
										opacity: 1,
									},
								},
								labels: {
									textAnchor: 'middle',
								},
							}}
							tickFormat={(t) => ''}
							animation={true}
						>
							<CircularGridLines
								tickTotal={6}
								tickValues={[...new Array(5)].map((v, i) => i / 5 - 1)}
								style={{ stroke: '#666262', strokeOpacity: 0.4 }}
							/>
						</RadarChart>
					</>
				)}
			</Flex>
		);
	}
}

export default RatingChart;
