import React from 'react';
import 'react-vis/dist/style.css';
import { SingletonSocket } from '../SingletonSocket';
import DrawD3BarChart from './D3Components/DrawD3BarChart';
import {
	XYPlot,
	XAxis,
	YAxis,
	VerticalGridLines,
	HorizontalGridLines,
	VerticalBarSeries,
	VerticalBarSeriesCanvas,
    LabelSeries,
    LineSeries,
} from 'react-vis';
import * as d3 from 'd3';
import { Spinner} from '@chakra-ui/react';
interface XYResponseStruct {
	x: string;
	y: number;
}
interface XYDataProps {
	graphdata: XYResponseStruct[];
	dataPipe: string;
	yAxisName: string;
	socketConnection: any;
}

class BarChart extends React.Component<
	{ dataPipe: string; yAxisName: string },
	XYDataProps
> {
	constructor(props) {
		super(props);
		this.state = {
			graphdata: [],
			dataPipe: this.props.dataPipe,
			yAxisName: this.props.yAxisName,
			socketConnection: SingletonSocket.getInstance().getSocket(),
		};
	}

	componentDidMount() {
		const { dataPipe, socketConnection } = this.state;
		socketConnection.on(dataPipe, (data) => {
			this.setState({ graphdata: data });
		});
	}

	render() {
		const { graphdata, yAxisName } = this.state;

		return (
			<div>
				{graphdata.length == 0 ? (
					<XYPlot
                        dontCheckIfEmpty={true}
						xType="time"
						width={750}
						height={500}
						animation={true}
						margin={{ bottom: 80, left: 50, right: 10, top: 20 }}
					></XYPlot>
				) : (
                        <XYPlot
                            dontCheckIfEmpty={true}
                            xDomain={[graphdata[0].x, graphdata[graphdata.length - 1].x]}
						xType="time"
						width={750}
						height={500}
						animation={true}
						margin={{ bottom: 80, left: 50, right: 10, top: 20 }}
					>
						<XAxis tickLabelAngle={-45} tickTotal={10} />
                            <YAxis tickFormat={(tick) => d3.format('.2s')(tick)} />
						<LineSeries
							curve={'curveMonotoneX'}
							className="vertical-bar-series-example"
							data={graphdata}
							opacity={1}
							strokeStyle="solid"
							style={{}}
						/>
					</XYPlot>
				)}
			</div>
		);
	}
}

export default BarChart;
