import {Socket} from 'dgram';
import React from 'react';
import {SALESDATA} from '../data';
import {SingletonSocket} from '../SingletonSocket';
import DrawD3BarChart from './D3Components/DrawD3BarChart';

interface XYResponseStruct {
	x: string;
	y: number;
}
interface XYDataProps {
	graphdata: XYResponseStruct[];
    dataPipe: string;
    yAxisName: string;
    socketConnection: any
}

class BarChart extends React.Component<
    {dataPipe: string; yAxisName: string;},
	XYDataProps
> {
	constructor(props) {
		super(props);
		this.state = {
			graphdata: [],
			dataPipe: this.props.dataPipe,
            yAxisName: this.props.yAxisName,
            socketConnection: SingletonSocket.getInstance().getSocket()
		};
	}
	componentDidMount() {
		const {dataPipe,socketConnection } = this.state;
        socketConnection.on(dataPipe, (data) => {
            this.setState({graphdata: data})
        });
	}
	render() {
        const {graphdata, yAxisName} = this.state;
