import React from 'react';
import {
	XYPlot,
	XAxis,
	YAxis,
	VerticalBarSeries,
    VerticalBarSeriesCanvas,
    DiscreteColorLegend,
    GradientDefs
} from 'react-vis';

function yDomain(data) {

 return data.reduce(
  (res, row) => {
    return {
      max: Math.max(res.max, row.y),
      min: Math.min(res.min, row.y)
    };
  },
  {max: -Infinity, min: Infinity}
    );
}
function FancyBarChart({ data}){

    const  useCanvas  = false
    const BarSeries = useCanvas ? VerticalBarSeriesCanvas : VerticalBarSeries;

    return (
			<div>
				<XYPlot
					margin={{ left: 110 }}
					xType="ordinal"
					width={1000}
					height={500}
				>
					<BarSeries className="vertical-bar-series-example" data={data} />
					<XAxis />
					<YAxis />
				</XYPlot>
				<svg height={0} width={0}>
					<GradientDefs>
						<pattern
							id="stripes"
							width="4"
							height="4"
							patternUnits="userSpaceOnUse"
						>
							<path d="M 0, 0 l 5, 5" stroke="#45aeb1" strokeLinecap="square" />
						</pattern>
						<pattern
							id="circles"
							width="3"
							height="3"
							patternUnits="userSpaceOnUse"
						>
							<circle cx="1.5" cy="1.5" r="0.8" fill="magenta" />
						</pattern>
					</GradientDefs>
				</svg>
				<DiscreteColorLegend
					orientation="horizontal"
					width={300}
					items={[{ title: 'Sales', color: 'url(#stripes)', strokeWidth: 1 }]}
				/>
			</div>
		);

}
export default FancyBarChart
