import React from 'react';
import { SALESDATA } from '../data';
import { SingletonSocket } from '../SingletonSocket';
import DrawD3BarChart from './D3Components/DrawD3BarChart';
import {
	XYPlot,
	XAxis,
	YAxis,
	VerticalGridLines,
	HorizontalGridLines,
	VerticalBarSeries,
	VerticalBarSeriesCanvas,
	LabelSeries,
} from 'react-vis';
import * as d3 from 'd3';
import { useTheme } from '@chakra-ui/react';
import { NavBarButtons } from './NavBarButtons';
import { formatWithOptions } from 'util';
import fonts from '../theme';
interface XYResponseStruct {
	x: string;
	y: number;
}
interface XYDataProps {
	graphdata: XYResponseStruct[];
	dataPipe: string;
	yAxisName: string;
	socketConnection: any;
}

class BarChart extends React.Component<
	{ dataPipe: string; yAxisName: string },
	XYDataProps
> {
	constructor(props) {
		super(props);
		this.state = {
			graphdata: [],
			dataPipe: this.props.dataPipe,
			yAxisName: this.props.yAxisName,
			socketConnection: SingletonSocket.getInstance().getSocket(),
		};
	}

	componentDidMount() {
		const { dataPipe, socketConnection } = this.state;
		socketConnection.on(dataPipe, (data) => {
			this.setState({ graphdata: data });
		});
	}

	render() {
		const { graphdata, yAxisName } = this.state;

		return (
			<div>
				<XYPlot
					xDomain={[SALESDATA[0].x,graphdata[graphdata.lastIndexOf(graph)].x]}
					xType="time"
					width={750}
					height={500}
					// xDistance={10}
					animate
					style={{ fontSize: fonts.fontSizes.sm }}
				>
					<VerticalGridLines />
					<HorizontalGridLines />
					<XAxis tickLabelAngle={-45} />
					<YAxis tickFormat={(tick) => d3.format('.2s')(tick)} />
					<VerticalBarSeries
						className="vertical-bar-series-example"
						data={SALESDATA}
					/>
				</XYPlot>
			</div>
		);
	}
}

export default BarChart;
