import {Socket} from 'dgram';
import React from 'react';
import {SALESDATA} from '../data';
import {SingletonSocket} from '../SingletonSocket';
import DrawD3BarChart from './D3Components/DrawD3BarChart';
import {
	XYPlot,
	XAxis,
	YAxis,
	VerticalGridLines,
	HorizontalGridLines,
	VerticalBarSeries,
	VerticalBarSeriesCanvas,
	LabelSeries,
} from 'react-vis';

interface XYResponseStruct {
	x: string;
	y: number;
}
interface XYDataProps {
	graphdata: XYResponseStruct[];
    dataPipe: string;
    yAxisName: string;
    socketConnection: any
}

class BarChart extends React.Component<
    {dataPipe: string; yAxisName: string;},
	XYDataProps
> {
	constructor(props) {
		super(props);
		this.state = {
			graphdata: [],
			dataPipe: this.props.dataPipe,
            yAxisName: this.props.yAxisName,
            socketConnection: SingletonSocket.getInstance().getSocket()
		};
	}
	componentDidMount() {
		const {dataPipe,socketConnection } = this.state;
        socketConnection.on(dataPipe, (data) => {
            this.setState({graphdata: data})
        });
	}
	render() {
        const {graphdata, yAxisName} = this.state;
        const labelData = SALESDATA.map((d, idx) => ({
					x: d.x,
					y: Math.max(SALESDATA[idx].y),
				}));

        return (
					<div>
						{/* <DrawD3BarChart data={SALESDATA} yAxisName={yAxisName}></DrawD3BarChart> */}
						<XYPlot xType="ordinal" width={750} height={500} xDistance={100} >
							<VerticalGridLines />
							<HorizontalGridLines />
							<XAxis />
							<YAxis />
                    <VerticalBarSeries
								className="vertical-bar-series-example"
								data={SALESDATA}
							/>
							<LabelSeries data={SALESDATA} getLabel={(d) => d.x} />
						</XYPlot>
					</div>
				);
	}
}

export default BarChart;
