import React from 'react';
import 'react-vis/dist/style.css';
import { SingletonSocket } from '../SingletonSocket';
import {
    RadialChart as RChart,
    CircularGridLines
} from 'react-vis';
import * as d3 from 'd3';
import { Box, Flex, Grid, GridItem, Skeleton } from '@chakra-ui/react';
import theme from '../theme';

// This radial chart is specifically only for showing scores out of 5.
interface ResponseStruct {
	angle: number
    radius: number
    label: string
    subLabel?:string
}

interface DataProps {
	dataStorage: ResponseStruct[];
	dataPipe: string[];
	socketConnection: any;
	value?: any;
	items?: any;
}

class RadialChart extends React.Component<
	{ dataPipe: string[]; height: number; width: number },
	DataProps
> {
	constructor(props) {
		super(props);
		var itemArray = [];
		for (let i = 0; i < this.props.dataPipe.length; i++) {
			itemArray.push({ title: this.props.dataPipe[i] });
		}
		this.state = {
			dataStorage:[],
			dataPipe: this.props.dataPipe,
			socketConnection: SingletonSocket.getInstance().getSocket(),
			items: itemArray,
			value: null,
		};
	}

	componentDidMount() {
        const {dataPipe, socketConnection, dataStorage} = this.state;
		for (let i = 0; i < dataPipe.length; i++) {
            socketConnection.on(dataPipe[i], (data) => {
                var scaledRadius = ((1/6*5) / 5) * data.radius
                dataStorage[i] = { angle:1, radius:scaledRadius,label:data.label }
				this.setState({ dataStorage: dataStorage });
			});
		}
	}
	componentWillUnmount() {
		const { dataPipe, socketConnection } = this.state;
		for (let i = 0; i < dataPipe.length; i++) {
			socketConnection.off(dataPipe[i], (data) => {});
		}
	}

	render() {
        const {dataStorage, value, items} = this.state;
        const myData = [
					{ angle: 1, radius: 0.16666666666, label: 'A' },
					{ angle: 1, radius: 0.2, label: 'B' },
					{ angle: 1, radius: 0.8, label: 'C' },
					{ angle: 1, radius: 1, label: 'D' },
        ];
        console.log(dataStorage)
		return (
			<Flex p="1">
				{dataStorage.length != this.props.dataPipe.length ? (
					<Skeleton
						height={this.props.height.toString() + 'px'}
						width={this.props.width.toString() + 'px'}
					></Skeleton>
				) : (
					<>
						<RChart
							width={this.props.width}
							height={this.props.height}
                                data={dataStorage}
							animation={true}
							labelsRadiusMultiplier={1.3}
							labelsStyle={{ fontSize: theme.fontSizes.sm, fill: '#222' }}
							showLabels={true}
							radius={this.props.width / 2}
						>
							<CircularGridLines
								tickTotal={6}
								tickValues={[...new Array(6)].map((v, i) => i / 6 - 1)}
								style={{ stroke: '#666262', strokeOpacity: 0.4 }}
							/>
						</RChart>
					</>
				)}
			</Flex>
		);
	}
}

export default RadialChart;
