import React from 'react';
// importing as NodeGraph because React also has Graph and gets confused, causing undefined type in React.createElement
import {Graph as NodeGraph} from 'react-d3-graph';
// data structure definitions for NodeGraph component
interface links {
	source: string;
	target: string;
}
interface nodes {
	id: string;
}
interface nodeGraphDataStruct {
	nodes: nodes[];
	links: links[];
}

function SocialNodeGraph({
	nodeGraphData,
}: {
	nodeGraphData: nodeGraphDataStruct;
    }) {
    const [shouldRender, setShouldRender] = React.useState(false);
		React.useEffect(() => {
			const timeout = setTimeout(() => setShouldRender(true), 1000);

			return () => clearTimeout(timeout);
		}, []);
    const myConfig = {
        nodeHighlightBehavior: true,
        // height: 500,
        // width: 1000,

		node: {
			color: 'steelblue',
			size: 120,
			highlightStrokeColor: 'blue',
		},
		link: {
			highlightColor: 'lightblue',
		},
	};

	const onClickNode = function (nodeId) {
		window.alert(`${nodeId} is probably human?`);
	};

	const onClickLink = function (source, target) {
		window.alert(`${source} is friends with ${target}`);
    };

    return (
        <If condition={shouldRender}>
		<NodeGraph
			id="socialNetGraph" // id is mandatory
			data={nodeGraphData}
			config={myConfig}
			onClickNode={onClickNode}
			onClickLink={onClickLink}
            />
        </If>
	);
}
export default SocialNodeGraph;
