import React from 'react';
import * as d3 from 'd3';
import { useD3 } from './hooks/useD3';
import { GradientDefs, DiscreteColorLegend } from 'react-vis';
import { ENDPOINT } from '../config';
import DrawD3BarChart from './DrawD3BarChart';
const socketIOClient = require('socket.io-client');
interface XYResponseStruct {
	x: string;
	y: number;
}
interface XYDataProps {
	graphdata: XYResponseStruct[];
	endpoint: string;
	dataPipe: string;
    yAxisName: string;
    socketConnection: SocketI
}

class BarChart extends React.Component<
	{ dataPipe: string; yAxisName: string },
	XYDataProps
> {
	constructor(props) {
		super(props);
		this.state = {
			graphdata: [],
			dataPipe: this.props.dataPipe,
			endpoint: ENDPOINT,
			yAxisName: this.props.yAxisName,
		};
	}
	componentDidMount() {
		const { endpoint, dataPipe } = this.state;
		const socket = socketIOClient(endpoint);
		socket.on(dataPipe, (data) => this.setState({ graphdata: data }));
	}
	render() {
		const { graphdata, yAxisName } = this.state;
        return (<>
            <DrawD3BarChart data={graphdata} yAxisName={yAxisName}></DrawD3BarChart>
        </>)
	}
}

export default BarChart;
